/**
 * 富士相机 EXIF 模板数据
 * 从 template.jpg 提取的完整 APP1 段
 * 
 * 包含以下关键标签：
 * - Make: FUJIFILM
 * - Model: FI028
 * - Software: Hybrid Instant Camera FI028 Ver1.02
 * - ExifVersion: 0230
 * - InternalSerialNumber: 92009865
 * - FujiFilmVersion: 0130
 * - 以及其他富士 MakerNotes 数据
 */

// 从 template.jpg 提取的 EXIF APP1 段 (base64 编码)
// 已移除缩略图和预览图，保留所有相机识别标签
const EXIF_APP1_BASE64 = "";

/**
 * 将 base64 字符串解码为 Uint8Array
 */
function base64ToUint8Array(base64) {
    const binary = atob(base64);
    const array = new Uint8Array(binary.length);
    for (let i = 0; i < binary.length; i++) {
        array[i] = binary.charCodeAt(i);
    }
    return array;
}

// 解码后的 EXIF APP1 数据
const EXIF_APP1_DATA = base64ToUint8Array(EXIF_APP1_BASE64);
